package main

import (
	"fmt"
	"strings"
)

var hop = 14

func main() {
	var idx = 0
	var jumps = 0

	for idx < len(input) {
		if jump := search(input[idx : idx+hop]); jump == 0 {
			fmt.Println(idx+hop, input[idx:idx+hop], "in", jumps, "jumps")
			return
		} else {
			idx += jump
			jumps++
		}
	}
}

func search(s string) int {
	fmt.Println("Searching", s)
	if len(s) <= 1 {
		return 0
	}

	if idx := strings.IndexByte(s[1:], s[0]); idx >= 0 {
		fmt.Println(s, "contains", string(s[0]), "at", idx+1)
		return hop - len(s) + 1
	}

	return search(s[1:])
}

